import React, { useState, useEffect } from "react";
import dogFacade from "../dogFacade";


const User = () => {

    //ADD DOG 
    const initdata = {
        "name": "",
        "dateOfBirth": "",
        "info": "",
        "breedName": ""
    }

    const [newDogData, setnewDogData] = useState(initdata);

    const perform = (evt) => {
        evt.preventDefault();
        dogFacade.addDog(newDogData, "user");
      }
      const onChange = (evt) => {
        setnewDogData({ ...newDogData, [evt.target.id]: evt.target.value })
      }

      //GetAllDogsByUser
      const initAllDogsdata = [{
        "id": "",  
        "name": "",
        "dateOfBirth": "",
        "info": "",
        "breedName": ""
    }]

    const [allDogData, setallDogData] = useState(initAllDogsdata);

      const performAllDogs = (evt) => {
        evt.preventDefault();
        dogFacade.getAllDogsByUser("user").then (data => setallDogData(data));
      }

      //DeleteDog

      const performDeleteDogs = (evt, id) => {
        evt.preventDefault();
        dogFacade.deleteDog(id).then(performAllDogs(evt));
      }

    return (
      <div>
          <h1>User page</h1>
          <br/>
        <h3>Add a dog</h3>
        <form onChange={onChange} >
          <input placeholder="UserName" id="userName"/>
          <input placeholder="Name" id="name"/>
          <input placeholder="DateOfBirth" id="dateOfBirth"/>
          <input placeholder="Info" id="info"/>
          <input placeholder="breedName" id="breedName"/>
          <button onClick={perform}>Add dog</button>
        </form>
        <br/>
        <h3>Get all my dogs</h3>
        <button onClick={performAllDogs}>Retrieve dogs</button>
        <ul>{allDogData.map (dog => <li key={dog.id}>{dog.name}, {dog.dateOfBirth}, {dog.info}, {dog.breedName} <button onClick={(event) => {performDeleteDogs(event, dog.id)}}>Delete</button></li>)}</ul>
      </div>
    );
  };

  export default User;
