import facade from './loginFacade';
import {baseURL} from "./settings";

function handleHttpErrors(res) {
  if (!res.ok) {
    return Promise.reject({ status: res.status, fullError: res.json() })
  }
  return res.json();
}

function demoFacade() {
  const fetchData = (url, method, body) => {
    const options = facade.makeOptions(method, true, body); //True add's the token
    return fetch(baseURL + url, options).then(handleHttpErrors);
  }
  const addDog = (newDog, username) => {
    const data = fetchData("api/dog/" + username, "POST", newDog);
    return data;
  }

  const getAllDogsByUser = (username) => {
    const data = fetchData("api/dog/" + username, "GET");
    return data;
  }


  return {
   addDog,
   getAllDogsByUser,

  }
}

const demo = demoFacade();
export default demo;
