import React, { useState, useEffect } from "react";
import facade from "../loginFacade";
import dogFacade from "../dogFacade";
import LogIn from './LogIn';
import LoggedIn from './LoggedIn';
import GetAllBreeds from './GetAllBreeds';

const Home = () => {
  const init = facade.loggedIn;
  const [loggedIn, setLoggedIn] = useState(init)

  const logout = () => {
    facade.logout()
    setLoggedIn(false)
  }
  const login = (user, pass) => {
    facade.login(user, pass)
      .then(res => setLoggedIn(true));
  }

  //GetAllBreeds

  const initBreedData = {
    "dogs": [
      {
        "breed": ""
      }
    ]
  }

  const [breedData, setbreedData] = useState(initBreedData);

  useEffect(() => {
    dogFacade.getAllBreeds().then(data => setbreedData(data));
  }, []);

  //GetAllBreedInformation

  const initBreedInformationData = {
    "breed": "",
    "info": "",
    "wikipedia": "",
    "image": "",
    "facts": [
      ""
    ]
  }

  const [breedInformationData, setbreedInformationData] = useState(initBreedInformationData);

  const performBreedInformation = (event, breed) => {
    event.preventDefault();
    dogFacade.getBreedInformation(breed).then(data => setbreedInformationData(data));
  }


  return (
    <div>
      <h1>Welcome</h1>
      {!loggedIn ? (<LogIn login={login} />) : (<LoggedIn logout={logout} />)}
      <br />
      <br />
      <h2>Information about dog breeds</h2>
      <img src={breedInformationData.image} alt={breedInformationData.image} width="300" height="200"></img>
      <ul>
        <li>Breed: {breedInformationData.breed}</li>
        <li>Info: {breedInformationData.info}</li>
        <li>Wikipedia: {breedInformationData.wikipedia}</li>
        <li>Image: {breedInformationData.image}</li>
        <li>Facts: {breedInformationData.facts}</li>
      </ul>
      <h3>List of dog breeds</h3>
      <ul>{breedData.dogs.map((element, index) => <li key={index}>{element.breed} <button onClick={(event) => { performBreedInformation(event, element.breed) }}>Retrieve information</button></li>)}</ul>
    </div>
  );
};

export default Home;
